    private function saveImage(string $fileName): string
    {
        $imagePath = public_path("assets/images/sample-berita/{$fileName}");
        $imageContent = file_get_contents($imagePath);
        $hashedName = md5($imageContent) . '.png';
        Storage::disk('public')->put($hashedName, $imageContent);
        return $hashedName;
    }
